import './MenuItem.css'


const MenuItem = ({id, type, price, qty, updateQty}) => {
    // Using the updateQty function that was passed down from the Menu component. The values for the "id" & "qty" that are passed in as arguments for the following functions are the values passed in for the "id" &"qty" props when each MenuItem is created
const addSlice = () => {
updateQty(id, qty + 1);
}

const removeSlice = () => {
    updateQty(id, qty -1);
}


    return (
        <div class='MenuItem'>
            <div>{type}</div>
            <div>${price}</div>
            <div>Qty: {qty}</div>
            <button onClick={addSlice}>+</button>
            {/* Using a ternary so the "removeSlice" function will NOT run when the qty is at zero (Note: "null" is used for the "else" in this Ternary so nothing happens if the condition is false) */}
            <button onClick={() => (qty > 0) ? removeSlice() : null}>-</button>
            <div>Total: ${price * qty}</div>
        </div>
    )
}

export default MenuItem;
