// STYLES IN REACT

// When importing a css file the ".css" file extension must be included
import './Styles.css'

const Styles = () => {
    return (
        // To style a React Component, a class name should be added to the top level/parent element. This allows for styles to be applied to the specific component.
        <div className='Styles'>
            {/* Inline Styling */}
            {/* Must use double brackets for inline styling in React. Also, style properties must be written in camelCase. Finally, commas are used to separate styles */}
            <h1 style={{color: 'blue', fontSize: '50px'}}>Hello from Styles Component</h1>
            {/* When creating a class name for an element inside the top level/parent element it is common practice to reference the class on the top level/parent element (Ex: Styles-Farewell) */}
            <h2 className='Styles-Farewell'>Goodbye</h2>
        </div>
    )
}

export default Styles;
